rows = [[letter for letter in line] for line in input.split("\n")]

def find_xmas(istart, istop, jstart, jstop, istep, jstep):
    total = 0
    for i in range(istart, istop):
        for j in range(jstart, jstop):
            if rows[i][j] == 'X' and rows[i+istep][j+jstep] == 'M' and rows[i+(2*istep)][j+(2*jstep)] == 'A' and rows[i+(3*istep)][j+(3*jstep)] == 'S':
                total += 1
    return total

